update.foreach:
UPDATE ITEM SET
    ITEMNAME =(
        CASE 
            WHEN ITEMNO=#{obj.itemno} THEN #{obj.itemname}	        	
	        END
    ), ITEMPRICE=(
        CASE 
            WHEN ITEMPRICE=#{obj.itemno} THEN #{obj.itemprice}
        END
    ), ITEMQTY=(
        CASE 
            WHEN ITEMQTY=#{obj.itemno} THEN #{obj.itemqty} 
        END
    ), ITEMDES=(
        CASE 
            WHEN ITEMDES=#{obj.itemno} THEN #{obj.itemdes}

        END
    )

WHERE ITEMNO IN ( #{obj.itemno} )
